import React, {useState} from 'react';

function Project(props) {
  const { title, image, description, liveLink, githubLink } = props;
  const [isHovered, setIsHovered] = useState(false);

  const handleHover = () => {
    setIsHovered(!isHovered);
  }


  return (
    <div className="project">
      <div className="project-image">
        <img src={image} alt={title} onClick={() => window.open(githubLink, '_blank')} onMouseEnter={handleHover} onMouseLeave={handleHover} />
        {isHovered && (
          <div className="project-overlay">
          <h3 className="project-title">{title}</h3>
        </div>
        )}
    
      </div>
      <div className="project-details">
        <p>{description}</p>
        <div className="project-links">
          <p><a href={liveLink} target="_blank" rel="noopener noreferrer">
            Live Demo
          </a></p>
         <p> <a href={githubLink} target="_blank" rel="noopener noreferrer">
            GitHub Repository
          </a></p>
        </div>
      </div>
    </div>
  );
}

export default Project;
